import React, { useState, useEffect, useRef } from "react";
import { useMediaQuery } from "@mui/material";
import Typography from "@mui/material/Typography";
import CircularProgress from "@mui/material/CircularProgress";

const Chart = () => {
    const [isLoaded, setIsLoaded] = useState(false);
    const iframeRef = useRef(null);
    const mobile = useMediaQuery("(max-width:500px)");
    const handleLoad = () => {
        setIsLoaded(true);
    };

    useEffect(() => {
        const iframe = iframeRef.current;
        if (iframe) {
            iframe.addEventListener("load", handleLoad);
            return () => iframe.removeEventListener("load", handleLoad);
        }
    }, []);

    return (
        <div style={{ marginBlock: '40px' }}>
            {!isLoaded && <div style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                marginBottom: '120px'
            }}>
                <Typography sx={{
                    marginBottom: "35px"
                }} variant="h6" component="h4">
                    Loading Chart...
                </Typography>
                <CircularProgress color="warning" />
            </div>}

            <iframe id="dextools-widget"
    title="DEXTools Trading Chart"
    width="500"
    height="400" src="https://www.dextools.io/widgets/en/ether/pe-light/0xa29fe6ef9592b5d408cca961d0fb9b1faf497d6d?theme=light&chartType=2&chartResolution=30&drawingToolbars=false"></iframe>
        </div>
    );
};

export default Chart;
