import { useState } from 'react';
import { ethers } from 'ethers';
import { PancakeSwap } from '@pancakeswap/sdk';
import { TradeType } from '@pancakeswap/sdk/dist/types';

function Swap() {
  const [inputToken, setInputToken] = useState('');
  const [outputToken, setOutputToken] = useState('');
  const [inputAmount, setInputAmount] = useState('');
  const [outputAmount, setOutputAmount] = useState('');

  const handleSwap = async () => {
    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const tradeType = TradeType.EXACT_INPUT;

      const inputTokenInfo = await PancakeSwap.fetchTokenData(inputToken);
      const outputTokenInfo = await PancakeSwap.fetchTokenData(outputToken);
      const inputAmountInWei = ethers.utils.parseUnits(inputAmount, inputTokenInfo.decimals);

      const pancakeSwap = PancakeSwap.create(inputTokenInfo, outputTokenInfo);
      const trade = await pancakeSwap.trade(inputAmountInWei, tradeType);

      const tx = await signer.sendTransaction({
        to: pancakeSwap.routerAddress,
        value: ethers.utils.parseEther('0'),
        data: trade.encodedData,
      });

      await tx.wait();
      setOutputAmount(ethers.utils.formatUnits(trade.outputAmount, outputTokenInfo.decimals));
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <input value={inputToken} onChange={(e) => setInputToken(e.target.value)} />
      <input value={outputToken} onChange={(e) => setOutputToken(e.target.value)} />
      <input value={inputAmount} onChange={(e) => setInputAmount(e.target.value)} />
      <button onClick={handleSwap}>Swap</button>
      <div>{outputAmount}</div>
    </div>
  );
}
