import React, { useRef, useEffect } from 'react';
import Paper from '@mui/material/Paper';
const Swap = () => {
  const iframeRef = useRef(null);
  useEffect(() => {
    // Get the content document of the iframe
    const iframeContent = iframeRef.current.contentDocument;

    // Apply styles to the body of the content document
    iframeContent.body.style.backgroundColor = 'lightblue';
    iframeContent.body.style.fontSize = '16px';
    iframeContent.body.style.fontFamily = 'Arial, sans-serif';
  }, []);
  return (
    <Paper sx={{
      mt: 7,
      p: 1,
      display: 'flex',
      flexDirection: 'column',
      height: 600,
      marginBottom: "70px"
    }}>
      <iframe ref={iframeRef}
        width="400"
        height="590"
        frameborder="0"
        allow="clipboard-read *; clipboard-write *; web-share *; accelerometer *; autoplay *; camera *; gyroscope *; payment *; geolocation *" src="https://flooz.trade/embed/trade?swapDisabled=false&swapToTokenAddress=0xdc42c3a92c4A03F9b9F3FBaBa0125286FDAa6772&swapLockToToken=true&onRampDisabled=true&onRampAsDefault=false&onRampDefaultAmount=200&network=bsc&lightMode=false&primaryColor=%23bfc500&roundedCorners=0&padding=32" ></iframe>
    </Paper>
  );
};

export default Swap;
