import { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { addresses, abis } from "@uniswap-v2-app/contracts";
import { useEthers } from "@usedapp/core";

const useWalletOfOwner = (contractAddress, ownerAddress) => {
  const [ids, setIds] = useState([]);
  const { account } = useEthers();
  useEffect(() => {
    const fetchIds = async () => {
      try {
        const provider = new ethers.providers.JsonRpcProvider(
            "https://data-seed-prebsc-1-s3.binance.org:8545"
        );
        const contract = new ethers.Contract(addresses.dsdc, abis.dsdc, provider);
        const ids = await contract.walletOfOwner(account);
        setIds(ids);
      } catch (error) {
        console.error(error);
      }
    };

    fetchIds();
  }, [account]);

  return ids;
};

export default useWalletOfOwner;
