import React, { useState } from 'react';
import { ThemeProvider } from 'styled-components'
import { Button, Card, CardBody, Flex, Heading, Text, Input, SwapVert } from '@pancakeswap-libs/uikit';
import { light, dark } from '@pancakeswap-libs/uikit'
import { ButtonPrimary } from '.';

const Swap = () => {
  const [fromToken, setFromToken] = useState('BNB');
  const [toToken, setToToken] = useState('CAKE');
  const [fromTokenAmount, setFromTokenAmount] = useState('');
  const [toTokenAmount, setToTokenAmount] = useState('');

  const handleFromTokenAmountChange = (e) => {
    setFromTokenAmount(e.target.value);
  };

  const handleToTokenAmountChange = (e) => {
    setToTokenAmount(e.target.value);
  };

  const handleSwap = () => {
    // Do the swap
    console.log(`Swapping ${fromTokenAmount} ${fromToken} for ${toTokenAmount} ${toToken}`);
  };

  const handleSwitchTokens = () => {
    // Switch the from and to tokens
    const tempToken = fromToken;
    const tempAmount = fromTokenAmount;
    setFromToken(toToken);
    setToToken(tempToken);
    setFromTokenAmount(toTokenAmount);
    setToTokenAmount(tempAmount);
  };

  return (
    <ThemeProvider theme={dark}>
      <Card>
        <CardBody>
          <Flex justifyContent="space-between" alignItems="center">
            <Heading>Swap</Heading>
            <Button onClick={handleSwitchTokens}>Switch</Button>
          </Flex>
          <Text>Exchange your tokens</Text>
          <Flex flexDirection="column" mt="24px">
            <Flex justifyContent="space-between">
              <Text>From</Text>
              <Text>{`Balance: ${fromToken} 100.0`}</Text>
            </Flex>
            <Flex alignItems="center">
              <Input
                type="number"
                placeholder="0.0"
                value={fromTokenAmount}
                onChange={handleFromTokenAmountChange}
                mt="8px"
                flex="1"
              />
              <Text ml="8px">{fromToken}</Text>
            </Flex>
            <Flex justifyContent="space-between" mt="16px">
              <Text>To</Text>
              <Text>{`Balance: ${toToken} 100.0`}</Text>
            </Flex>
            <Flex my="24px"  alignItems="center">
              <Input
                type="number"
                placeholder="0.0"
                value={toTokenAmount}
                onChange={handleToTokenAmountChange}
                mt="8px"
                flex="1"
              />
              <Text ml="8px">{toToken}</Text>
            </Flex>
            <ButtonPrimary onClick={handleSwap}>
              Swap
            </ButtonPrimary>
          </Flex>
        </CardBody>
      </Card>
    </ThemeProvider>
  );
};

export default Swap;
