import React, { useState } from 'react';
import { Button, Card, CardBody, Flex, Heading, Text, Input } from '@pancakeswap-libs/uikit';

const Swap = () => {
  const [fromTokenAmount, setFromTokenAmount] = useState('');
  const [toTokenAmount, setToTokenAmount] = useState('');

  const handleFromTokenAmountChange = (e) => {
    setFromTokenAmount(e.target.value);
  };

  const handleToTokenAmountChange = (e) => {
    setToTokenAmount(e.target.value);
  };

  const handleSwap = () => {
    // Do the swap
    console.log(`Swapping ${fromTokenAmount} for ${toTokenAmount}`);
  };

  return (
    <Card>
      <CardBody>
        <Heading>Swap</Heading>
        <Text>Exchange your tokens</Text>
        <Flex flexDirection="column" mt="24px">
          <Input
            type="number"
            placeholder="0.0"
            value={fromTokenAmount}
            onChange={handleFromTokenAmountChange}
            mt="8px"
          />
          <Input
            type="number"
            placeholder="0.0"
            value={toTokenAmount}
            onChange={handleToTokenAmountChange}
            mt="8px"
          />
          <Button mt="24px" width="100%" onClick={handleSwap}>
            Swap
          </Button>
        </Flex>
      </CardBody>
    </Card>
  );
};

export default Swap;
