import { Contract } from "@ethersproject/contracts";
import { useCall, useEthers } from "@usedapp/core";

export function useGetIsApprovedForAll(
  mutantsAddress,
  mutantsAbi,
  stakingAddress,
  address
) {
  const { value, error } =
    useCall(
      address &&
        mutantsAddress && {
          contract: new Contract(mutantsAddress, mutantsAbi), // instance of called contract
          method: "isApprovedForAll", // Method to be called
          args: [address, stakingAddress], // Method arguments - address to be checked for balance
        }
    ) ?? {};
  if (error) {
    console.error(error.message);
    return undefined;
  }
  
  return value?.[0];
}
