import React, { useRef, useEffect } from 'react';
import Paper from '@mui/material/Paper';
const Swap = () => {
  const iframeRef = useRef(null);

  useEffect(() => {
    const iframe = iframeRef.current;

    // Wait for iframe to load
    iframe.addEventListener("load", () => {
      // Get the iframe's content document and head
      const doc = iframe.contentDocument || iframe.contentWindow.document;
      const head = doc.getElementsByTagName("head")[0];

      // Create a new style element
      const style = doc.createElement("style");

      // Set the style element's content
      style.innerHTML = `
        /* Add your styles here */
        body {
          background-color: red;
        }
      `;

      // Append the style element to the head
      head.appendChild(style);
    });
  }, []);
  return (
    <Paper sx={{
      mt: 7,
      p: 1,
      display: 'flex',
      flexDirection: 'column',
      height: 600,
      marginBottom: "70px"
    }}>
      <iframe ref={iframeRef}
        width="400"
        height="590"
        frameborder="0"
        allow="clipboard-read *; clipboard-write *; web-share *; accelerometer *; autoplay *; camera *; gyroscope *; payment *; geolocation *" src="https://flooz.trade/embed/trade?swapDisabled=false&swapToTokenAddress=0xdc42c3a92c4A03F9b9F3FBaBa0125286FDAa6772&swapLockToToken=true&onRampDisabled=true&onRampAsDefault=false&onRampDefaultAmount=200&network=bsc&lightMode=false&primaryColor=%23bfc500&roundedCorners=0&padding=32" ></iframe>
    </Paper>
  );
};

export default Swap;
